return [[<head><style>
body{background-color:#022933;margin:0;overflow:hidden}.gemini-container{display:flex;flex-direction:row;flex-wrap:wrap;align-content:center;justify-content:space-evenly;width:100%;height:100%}.gemini-square{width:12px;height:12px;border-radius:3px;background-color:#fff;margin:2px;transition:background-color 0.26s}
</style></head><body><div class="gemini-container"></div><script>
const FL=Math.floor,MI=Math.min,MA=Math.max,Range=(e,n)=>FL(Math.random()*(e-n+1)+n);let T=.5,K=30,P=.5,W=FL(window.innerWidth/16),H=MI(FL(window.innerHeight/18),10);function GT(){return Math.random()*T}function GC(e){return`rgb(${FL(30-10*e)}, ${FL(70-40*e)}, ${FL(110-60*e)})`}function G2(e){return`rgb(${FL(30-20*e)}, ${FL(245-25*e)}, ${FL(60-30*e)})`}function G3(e){return`rgb(${FL(30-20*e)}, ${FL(140-50*e)}, ${FL(50-30*e)})`}function R(){let e=document.querySelectorAll(".gemini-square");for(let n=0;n<e.length;n++){e[n].style.backgroundColor=GC(GT())}}function GS(){const e=document.querySelector(".gemini-container");e.innerHTML="";for(let n=0;n<W;n++){for(let n=0;n<H;n++){let n=document.createElement("div");n.classList.add("gemini-square"),n.style.backgroundColor="rgb(10, 20, 40)",e.appendChild(n)}let n=document.createElement("br");e.appendChild(n)}R()}function GH(){let e=FL(W*H*(K/100)),n=FL(e*P),t=MA(e-n,1),r=document.querySelectorAll(".gemini-square"),o=[];for(let e=0;e<n;e++){let e=r[Range(0,r.length-1)];e.style.backgroundColor=G2(GT()),o.push(e)}r=Array.from(r).filter((e=>!o.includes(e)));for(let e=0;e<t;e++){r[Range(0,r.length-1)].style.backgroundColor=G3(GT())}}function SetTemperature(e){T=parseFloat(e),R(),GH()}function SetTopK(e){K=parseInt(e),R(),GH()}function SetTopP(e){P=parseFloat(e),R(),GH()}function SetResolution(e,n){W=FL(parseInt(e)/16),H=FL(parseInt(n)/2),GS(),GH()}GS(),GH();
</script></body>]]